import { RecipeCard } from "../components/RecipeCard"
import { useEffect, useState } from "react";
import { api } from "../util/api";
import { Recipe } from "../types";
import { parseTheMealDBToRecipes } from "../util/helpers";
import "https://js.supertab.co/v1/tab.js";

export const AllRecipes = () => {
  const [recipes, setRecipes] = useState<Recipe[]>([]);

  window.supertab.createPurchaseButton({
    containerElement: document.getElementById("purchase-recipe"),
    clientId: "client.cbe31267-317e-4ca5-bc66-aca83ce27d1d",
    merchantName: "Yummy recipes",
    merchantLogoUrl: "YOUR_LOGO_URL",
    offeringId: "offering.a05c3fad-b9d0-440a-9331-25e3f1b3be9c", // optional
    onPurchaseCompleted: () => {
      //insert your code to grant user access
      console.log("Purchase completed!");
    },
    onPurchaseCanceled: () => {
      //insert your code to handle when purchase is not completed
      console.log("Purchase canceled!");
    },
    onError: () => {
      //insert your code to handle an unexpected error
      console.log("Purchase error!");
    },
  });

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const { meals } = await api.theMealDB.getRecipes();
        const parsed = parseTheMealDBToRecipes(meals);
        setRecipes(parsed);
      } catch {
        console.log('Failed to fetch recipes');
      }
    }
    fetchRecipes();
  }, []);

  return(
    <>
      <header className="heading">
        All Recipes
      </header>
      <div className="grid-container">
        {recipes?.map(r => <RecipeCard recipe={r} key={`${r.id}-card`}/>)}
      </div>
    </>
  )
}
